import React, { Component, useState } from 'react';
import './cameraStyles.css'
import Webcam from "react-webcam";
import fs from 'fs'

const WebcamComponent = () => <Webcam />;



const videoConstraints = {
    width: 260,
    height: 260,
    facingMode: "user"
  };

  export const WebcamCapture = () => {
    const webcamRef = React.useRef(null);
    const [image,setImage]=useState('');

    const capture = React.useCallback(
      () => {
        const imageSrc = webcamRef.current.getScreenshot();
        // console.log(imageSrc)
        setImage(imageSrc)


    var url = "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAHElEQVQI12P4//8/w38GIAXDIBKE0DHxgljNBAAO9TXL0Y4OHwAAAABJRU5ErkJggg=="

    fetch(url)
    .then(res => res.blob())
    .then(blob => {
      var fd = new FormData()
      fd.append('image', blob, 'filename')

      console.log(blob)
    })


        // fetch('https://speakupgenderapi.herokuapp.com/predict_api',
        // {
        //     mode:'no-cors',
        //     method:'POST',
        //     body:JSON.stringify({file:imageSrc})
        // }).then(res=>console.log(res)).catch(err=>console.log(err))
      //   let base64Image = imageSrc.split(';base64,').pop();
      //   console.log(base64Image)
      //   fs.writeFile('image.png', base64Image, {encoding: 'base64'}, function(err) {
      //     console.log('File created');
      // });
      [webcamRef]
    });


    function dataURItoBlob(dataURI) {
      var binary = atob(dataURI.split(',')[1]);
      var array = [];
      for(var i = 0; i < binary.length; i++) {
          array.push(binary.charCodeAt(i));
      }
      return new Blob([new Uint8Array(array)], {type: 'image/jpeg'});
  }


    const [photo,setPhoto]=useState("");


    const uploadImage = (e) =>
    {
        //   setLoading(true)
          e.preventDefault();

          if(!photo)
          {

            // setLoading(false);
            alert("Upload a user image!");
            return;
          }

          var url=(URL.createObjectURL(photo));
          console.log(photo);
          fetch('https://speakupgenderapi.herokuapp.com/predict_api',
        {
            mode:'no-cors',
            method:'POST',
            body:JSON.stringify({file:photo})
        }).then(res=>res.json()).then(res=>console.log(res)).catch(err=>console.log(err))
        //   setLoading(false);
        //   alert("Image Uploaded");


    }

return (
          <>
          {/* <input type="file" 
          onChange={(event)=>{setPhoto(event.target.files[0])}} 
          accept=".jpg,.png,.jpeg" />
          <button onClick={uploadImage}>upload</button> */}
            <Webcam
              audio={false}
              height={260}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              width={260}
              videoConstraints={videoConstraints}
              download
            />
            <button onClick={capture}>Capture photo</button>
            {image!=''?<img src={image}/>:''}
          </>
        );
      };
