import React, { useContext, useState } from 'react'
import './signupstyles.css'
import $ from 'jquery'
import { useHistory } from 'react-router'
import signupIcon from './../../images/signupIcon.png'
import {UserContext} from '../../App'
import { Link } from 'react-router-dom'
// import {Camera} from '../camera'
import { WebcamCapture } from '../camera-feed'
import Camera from 'react-camera';




const Signup = () => {



    const history=useHistory();

    const [email,setEmail]=useState('');
    const [password,setPassword]=useState('');
    const [name,setName]=useState('');


    const submitSignup =(e)=>
    {

        if(email=='' || password=='' || name=='' || 
        !/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(String(email).toLowerCase()) )
        {
            alert("Please fill all the fields correctly!")
            return;
        }

        e.preventDefault();
        // var a=(URL.createObjectURL(photo))
        const formData=new FormData();
        // formData.append('userImage',photo);
        formData.append('name',name);
        formData.append('email',email);
        formData.append('password',password);

        fetch("http://localhost:500/signup",
        {
            headers:
            {
                "Content-Type":"application/json"
            },
            method:"post",
            body:formData
        }).then(res=>res.json())
        .then(res=>
            {
                    console.log(res)
                    if(!res.error)
                    {
                        // console.log(res)
                        history.push("/signin")
                    }
                    else
                    {
                         alert(res.error)
                    }
            })
        .catch(err=>alert(err))
    }



    const [userImg,setUserImg]=useState('')


    const uploadImage = (photo) =>
     {
       var a=(URL.createObjectURL(photo))


      const formData=new FormData();
        formData.append('userImage',photo);
      fetch('http://localhost:5000/capture',
      {
          method:'POST',
          body:formData
      }).then(res=>res.json())
      .then(async res=>
        {
            console.log(res.src)
            setUserImg(res.src.replace("public", ""));
            // genderVerify(res.src.replace("public", ""))
        }).catch(err=>console.log(err))
    // deleteImage();

     }

    return (
        <div className="signup-container">
            <div className="container">
            <div className="signupDesign"></div>

                <div className="signupText">
                <h1>Welcome to Speak Up!<span className="icon"><img src={signupIcon} width="50px" 
                height="40px"/></span></h1>
            <form className="form">
                <input type="text" placeholder="Name" onChange={(e)=>setName(e.target.value)}/>
                <input type="email" placeholder="Email" onChange={(e)=>setEmail(e.target.value)}/>
                <input type="password" placeholder="Password" onChange={(e)=>setPassword(e.target.value)}/>
                <button type="submit" id="login-button" onClick={(e)=>submitSignup(e)}>Signup</button>
                </form>

                <p className="redirect">Already have an account?
                    <Link to="/signin" className="loginLink">Login here..</Link></p>


                </div>


            </div>
        {/* <WebcamCapture uploadImage={uploadImage}/>
        {userImg!=''?<img src={userImg}/>:''} */}
            {/* <Camera/> */}
        </div>
    )
}


export default Signup
