import React, { useEffect, useState } from 'react'
import './poststyles.css';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons'
import { faThumbsDown } from '@fortawesome/free-solid-svg-icons'
import { Link } from 'react-router-dom';

const PostGrid = () => {


    const [allposts,setAllPosts]=useState('');
    const [likes,setLikes]=useState('');

    useEffect(
        async()=>{
            await fetch("http://localhost:5000/allPosts",
            {
              headers:
              {
                "Content-Type":"application/json",
                "Authorization":"Bearer"+localStorage.getItem("jwt")
              },
              method:"get"

            })
            .then(res=>res.json())
            .then(res=>
              {
                setAllPosts(res.posts);
              })
            .catch(err=>console.log(err))
    },[]);


    const increment = async(id) =>
    {

        await fetch("http://localhost:5000/like",
           {
             method:"put",
             headers: 
             {
               "Content-Type":"application/json",
               "Authorization":"Bearer"+localStorage.getItem("jwt")

             },
             body:JSON.stringify(
                  {
                      post_id:id
                  }
                )

           }).then(res=>res.json())
           .then(result=>
            {
                setAllPosts(result.posts)
            }).catch(err=>alert(err))

    }

    const decrement = async(id) =>
    {

        await fetch("http://localhost:5000/unlike",
           {
             method:"put",
             headers: 
             {
               "Content-Type":"application/json",
               "Authorization":"Bearer"+localStorage.getItem("jwt")

             },
             body:JSON.stringify(
                  {
                      post_id:id
                  }
                )

           }).then(res=>res.json())
           .then(result=>
            {
                setAllPosts(result.posts)
            }).catch(err=>alert(err))

    }

    const PostCard = ({post})=>
    {
        return (

            <div className="col cards-col col-lg-4 col-md-6 mb-4">
            <div className="card">
            <div className="card-body">
              <h6 className="text-secondary" style={{float:'right'}}>Anonymous</h6>
                <h3 className="card-title">{post.title}</h3>
                <p className="card-text">{post.body}</p>
                <button className="btn" style={{marginRight:'5px'}}>
                <FontAwesomeIcon icon={faThumbsUp} className="icon" onClick={()=>increment(post._id)}/>
                </button>
                <button className="btn">
                <FontAwesomeIcon icon={faThumbsDown} className="icon" onClick={()=>decrement(post._id)}/>
                </button>
                <label style={{float:'right'}}>{post.likes} Likes</label>
            </div>
            </div>
            </div>
        )
    }


    return (

        <div className="post-container">
         <nav className="navbar navbar-expand-lg navbar-dark">
          <div style={{display:'flex',justifyContent:'center',alignItems:'center'}}>
          <Link to="/">
                      <span><img src="https://miro.medium.com/max/3840/1*gYptxAgBRVHvobYE8WBxJQ.png" height="50px" width="50px"
                      style={{marginTop:'-10px'}}/></span>
                      <a className="navbar-brand" href="#"><h2 style={{marginLeft:'10px'}}>Speak Up</h2></a>
                      </Link>
            <div  >
              <ul className="navbar-nav">
                <li className="nav-item">
                  <Link to="/add"><a className="nav-link" style={{color:'white'}}>Add</a></Link>
                </li>
                <li className="nav-item">
                  <Link to="/grid"><a className="nav-link" style={{color:'white'}}>Grid</a></Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>
         <div class="container homegrid">
            <div className="row">
                {allposts!='' && allposts.map((post)=>
                {
                  return(
                     <PostCard post={post}/>
                  )
                })}
                {allposts==''?<h1>Add Posts</h1>:''}
            </div>
            </div>
        </div>
    )
}

export default PostGrid
