import React, { useEffect, useState } from 'react'
import Navbar from '../Navbar/Navbar';
import './styles.css';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons'

const PostGrid = () => {


    const [allposts,setAllPosts]=useState('');

    useEffect(
        async()=>{
            await fetch("http://localhost:5000/allPosts",
            {
              headers:
              {
                "Content-Type":"application/json",
              },
              method:"get"

            })
            .then(res=>res.json())
            .then(res=>
              {
                setAllPosts(res.posts);
              })
            .catch(err=>console.log(err))
    },[]);


    const increment = (id) =>
    {
        fetch("/like",
           {
             method:"put",
             headers: 
             {
               "Content-Type":"application/json",
               "Authorization":"Bearer"+localStorage.getItem("jwt")

             },
             body:JSON.stringify(
                  {
                      post_id:id
                  }
                )

           }).then(res=>res.json())
           .then(result=>
            {
              console.log(result )
              const newData = data.map ( item => 
                {
                  if(item._id == result._id)
                  {
                    return result
                  }
                  else
                  {
                    return item
                  }
                })

                setData(newData);
            }).catch(err=>console.log(err))

    }

    const PostCard = ({post})=>
    {
        return (
            <div className="col" xs={6}  style={{marginBottom:'5rem'}}>
            <div className="card" style={{width: '19rem',height:'12rem'}}>
            <div className="card-body">
                <h3 className="card-title">{post.title}</h3>
                <p className="card-text">{post.body}</p>
                <button className="btn">
                <FontAwesomeIcon icon={faThumbsUp} onClick={()=>increment()}/>
                </button>
            </div>
            </div>
            </div>
        )
    }


    return (

        <div className="post-container">
        <Navbar/>
        <div className="homegrid">
            <div className="container">
            <div className="row">
                {allposts!='' && allposts.map((post)=>
                {
                  return(
                     <PostCard post={post}/>
                  )
                })}
            </div>
            </div>
        </div>
        </div>
    )
}

export default PostGrid
