    return (
      <div className="add-container">
        <nav className="navbar navbar-expand-lg navbar-dark">
        <div style={{display:'flex',justifyContent:'center',alignItems:'center'}}>
        <Link to="/"><a className="navbar-brand" href="#"><h2>Speak Up</h2></a></Link>
          <div  >
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link to="/add"><a className="nav-link" style={{color:'white'}}>Add</a></Link>
              </li>
              <li className="nav-item">
                <Link to="/grid"><a className="nav-link" style={{color:'white'}}>Grid</a></Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <h1 className="title">Share your story with us!</h1>
        <div className="form-container">
            <div className="mb-3">
            <label for="exampleFormControlInput1" className="form-label" className="labels">Title</label>
            <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Enter the title"
            onChange={(e)=>
              {
                 e.preventDefault();
                 setTitle(e.target.value)
              }}/>
