import React, { useContext, useEffect, useState } from 'react'
import './poststyles.css';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faThumbsUp } from '@fortawesome/free-solid-svg-icons'
import { faThumbsDown } from '@fortawesome/free-solid-svg-icons'
import { Link, useHistory } from 'react-router-dom';
import { UserContext } from '../../App';
import Nav from '../nav/Nav';

const PostGrid = () => {


    const [allposts,setAllPosts]=useState('');
    const [likes,setLikes]=useState('');

    const { state, dispatch } = useContext(UserContext);


    const history=useHistory();

    useEffect(
        async()=>{
            await fetch("http://localhost:5000/allPosts",
            {
              headers:
              {
                "Content-Type":"application/json",
                "Authorization":"Bearer"+localStorage.getItem("jwt")
              },
              method:"get"

            })
            .then(res=>res.json())
            .then(res=>
              {
                setAllPosts(res.posts);
              })
            .catch(err=>console.log(err))
    },[]);


    const increment = async(id) =>
    {

        await fetch("http://localhost:5000/like",
           {
             method:"put",
             headers: 
             {
               "Content-Type":"application/json",
               "Authorization":"Bearer"+localStorage.getItem("jwt")

             },
             body:JSON.stringify(
                  {
                      post_id:id
                  }
                )

           }).then(res=>res.json())
           .then(result=>
            {
                setAllPosts(result.posts)
            }).catch(err=>alert(err))

    }

    const decrement = async(id) =>
    {

        await fetch("http://localhost:5000/unlike",
           {
             method:"put",
             headers: 
             {
               "Content-Type":"application/json",
               "Authorization":"Bearer"+localStorage.getItem("jwt")

             },
             body:JSON.stringify(
                  {
                      post_id:id
                  }
                )

           }).then(res=>res.json())
           .then(result=>
            {
                setAllPosts(result.posts)
            }).catch(err=>alert(err))

    }

    const PostCard = ({post})=>
    {
        return (

        <div class="card text-black h-100">
                <img class="card-img" src="https://image.freepik.com/free-vector/hand-drawn-womens-day-concept_23-2148435898.jpg" 
                alt="Card image"/>
                <div class="card-img-overlay">
                <h6 className="text-secondary" style={{float:'right'}}>Anonymous</h6>
                          <h3 className="card-title">{post.title}</h3>
                          <p className="card-text">{post.body}</p>
                          <button className="btn" style={{marginRight:'5px',color:'black'}}>
                          <FontAwesomeIcon icon={faThumbsUp} className="icon" onClick={()=>increment(post._id)}/>
                          </button>
                          <button className="btn">
                          <FontAwesomeIcon icon={faThumbsDown} className="icon" onClick={()=>decrement(post._id)}/>
                          </button>
                          <label style={{float:'right'}}>{post.likes} Likes</label>
                </div></div>
        )
    }


    return (
<div className="gridContainer">
        <Nav/>
         <div className="homegrid">
            {/* <div className="card-columns">
                {allposts!='' && allposts.map((post)=>
                {
                  return(
                     <PostCard post={post}/>
                  )
                })}
                {allposts==''?<h1>Add Posts</h1>:''}
            </div> */}
            <div class="card-columns">
  <div class="card">
    <img class="card-img-top" src=".../100px160/" alt="Card image cap"/>
    <div class="card-body">
      <h5 class="card-title">Card title that wraps to a new line</h5>
      <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
    </div>
  </div>
  <div class="card p-3">
    <blockquote class="blockquote mb-0 card-body">
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere erat a ante.</p>
      <footer class="blockquote-footer">
        <small class="text-muted">
          Someone famous in <cite title="Source Title">Source Title</cite>
        </small>
      </footer>
    </blockquote>
  </div>
  <div class="card">
    <img class="card-img-top" src=".../100px160/" alt="Card image cap"/>
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">This card has supporting text below as a natural lead-in to additional content.</p>
      <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
    </div>
  </div>
  <div class="card bg-primary text-white text-center p-3">
    <blockquote class="blockquote mb-0">
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere erat.</p>
      <footer class="blockquote-footer">
        <small>
          Someone famous in <cite title="Source Title">Source Title</cite>
        </small>
      </footer>
    </blockquote>
  </div>
  <div class="card text-center">
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">This card has a regular title and short paragraphy of text below it.</p>
      <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
    </div>
  </div>
  <div class="card">
    <img class="card-img" src=".../100px260/" alt="Card image"/>
  </div>
  <div class="card p-3 text-right">
    <blockquote class="blockquote mb-0">
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer posuere erat a ante.</p>
      <footer class="blockquote-footer">
        <small class="text-muted">
          Someone famous in <cite title="Source Title">Source Title</cite>
        </small>
      </footer>
    </blockquote>
  </div>
  <div class="card">
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">This is another card with title and supporting text below. This card has some additional content to make it slightly taller overall.</p>
      <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
    </div>
  </div>
</div>
            </div>
            </div>
    )
}

export default PostGrid
