import React, { useState } from 'react'
import { Link } from 'react-router-dom';

import { useHistory } from 'react-router'
import './addstyles.css';

const Add = () => {


    //handle innputs
    const [title,setTitle]=useState('');
    const [body,setBody]=useState('');


   const history=useHistory();

    const submitForm = async (e) =>
    {
        e.preventDefault();
        var data=JSON.stringify({title,body});
        await fetch("http://localhost:5000/addPost",
        {
          headers:
          {
            "Content-Type":"application/json",
            "Authorization":"Bearer"+localStorage.getItem("jwt")
          },
          method:"post",
          body:data

        })
        .then(res=>res.json())
        .then(res=>
          {
            console.log(res);
            history.push('/grid')
          })
        .catch(err=>alert(err))
    }

    return (
      <div className="add-container">
        <nav className="navbar navbar-expand-lg navbar-dark">
        <div style={{display:'flex',justifyContent:'center',alignItems:'center'}}>
        <Link to="/">
                <span><img src="https://miro.medium.com/max/3840/1*gYptxAgBRVHvobYE8WBxJQ.png" height="50px" width="50px"
                style={{marginTop:'-10px'}}/></span>
                <a className="navbar-brand" href="#"><h2 style={{marginLeft:'10px'}}>Speak Up</h2></a>
                </Link>
          <div  >
            <ul className="navbar-nav">
              <li className="nav-item">
                <Link to="/add"><a className="nav-link" style={{color:'white'}}>Add</a></Link>
              </li>
              <li className="nav-item">
                <Link to="/grid"><a className="nav-link" style={{color:'white'}}>Grid</a></Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <h1 className="title">Share your story with us!</h1>
        <div className="form-container">
            <div className="mb-3">
            <label for="exampleFormControlInput1" className="form-label" className="labels">Title</label>
            <input type="text" className="form-control" id="exampleFormControlInput1" placeholder="Enter the title"
            onChange={(e)=>
              {
                 e.preventDefault();
                 setTitle(e.target.value)
              }}/>
          </div>
          <div className="mb-3">
            <label for="exampleFormControlTextarea1" className="form-label" className="labels">Post Body</label>
            <textarea className="form-control" id="exampleFormControlTextarea1" rows="6"
            placeholder="Share your story.."
            onChange={(e)=>
            {
               e.preventDefault();
               setBody(e.target.value)
            }}></textarea>
          </div>
          <button type="button" class="btn btn-outline" id="submit" 
          onClick={(e)=>submitForm(e)}
          >
          Submit</button>
        </div>
      </div>
    )
}

export default Add
